(function(){CKEDITOR.on("instanceReady",function(e){var e=e.editor,f=CKEDITOR.document.$.getElementsByName("ckeditor-sample-required-plugins"),f=f.length?CKEDITOR.dom.element.get(f[0]).getAttribute("content").split(","):[],h=[];if(f.length){for(var g=0;g<f.length;g++){e.plugins[f[g]]||h.push("<code>"+f[g]+"</code>")}h.length&&CKEDITOR.dom.element.createFromHtml('<div class="warning"><span>To fully experience this demo, the '+h.join(", ")+" plugin"+(1<h.length?"s are":" is")+" required.</span></div>").insertBefore(e.container)}})})();