describe("jquery.pwstrength",function(){function a(){$("form").html('<input type="password" id="field" data-indicator="indicator"><div id="indicator"><div class="label"></div></div>');return $("#field").pwstrength()}it("detects very weak passwords",function(){expect($.pwstrength("abcd")).toEqual(0)});it("detects weak passwords",function(){expect($.pwstrength("abdge123")).toEqual(1)});it("detects mediocre passwords",function(){expect($.pwstrength("Ab12Degf")).toEqual(2)});it("detects strong passwords",function(){expect($.pwstrength("@b12Degf")).toEqual(3)});it("detects very strong passwords",function(){expect($.pwstrength("@b12De Fghbe#")).toEqual(4);expect($.pwstrength("@b12DeFghbe234567#")).toEqual(4)});it("updates the strength indicator",function(){var b=a(),c=$("#indicator");b.val("abcd");b.keyup();expect(c.hasClass("pw-very-weak")).toBeTruthy();expect(c.find(".label").html()).toEqual("very weak");b.val("@b12De Fghbe#");b.keyup();expect(c.hasClass("pw-very-strong")).toBeTruthy();expect(c.hasClass("pw-very-weak")).toBeFalsy();expect(c.find(".label").html()).toEqual("very strong")})});